                            // Prevents the page from reloading
                            event.preventDefault();

                            proceeding_1()
                        });
                        document.querySelector(proceed2_id).addEventListener('click', function () {
                            // Prevents the page from reloading
                            event.preventDefault();

                            proceeding_2()
                        });
                        document.querySelector(proceed3_id).addEventListener('click', function () {
